package chap01;

public class p39 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x;     // 정수형 변수 x 선언
		x = 1;     // 정수형 변수 x에 1을 저장
		int y =2;  // 정수형 y에 정수 2를 저장 
		int z = x + y;  // 정수형 변수 z에 x + y값을 저장
		  System.out.println("z");     // string type "z"를 출력
		  System.out.println(z);       // int z 값 3을 출력
		  System.out.println("z=" + z);  // 결과는 z=3 출력
		  

	}

}
